var a = @{actglobal@{parent.variables.n}.value}
var b = @{trigglobal@{parent.variables.n}.value}
var c = @{chaostrigglobal@{parent.variables.n}.value}

var d = a + b + c
var output = "false"

if (d == 0) {
output = "false"
} else {
output = "true"
}

return [output]
}}

